import { fabric } from 'fabric';
export default {
  name: 'imageUpload',
  props: {
    canW: {
      type: [String, Number],
      required: true
    },
    canH: {
      type: [String, Number],
      required: true
    }
  },

  data() {
    return {
      item: {
        image: null,
        imageUrl: null
      },
      canvas: null
    };
  },

  mounted() {
    this.canvas = new fabric.Canvas(this.$refs.can);
    this.canvas.setDimensions({
      width: this.canW,
      height: this.canH
    });
    this.canvas.setBackgroundImage(this.item.imageUrl, this.canvas.renderAll());
  },

  methods: {
    onChange(e) {
      const file = e.target.files[0];
      this.image = file;
      this.item.imageUrl = URL.createObjectURL(file);
    }

  }
};
